import React from "react";

const Footer = ({ darkMode, toggleDarkMode }) => {
  return (
    <footer className="bg-[#402E7A] dark:bg-gray-800 text-white dark:text-gray-300 py-4 shadow-md w-full mt-auto">
      <div className="max-w-4xl mx-auto flex justify-between items-center">
        <p className="text-lg">&copy; 2024 FizzBuzzBass. All Rights Reserved.</p>

        <button
          onClick={toggleDarkMode}
          className="px-4 py-2 bg-gray-200 dark:bg-gray-600 text-black dark:text-white rounded transition-colors duration-300"
        >
          {darkMode ? 'Light Mode' : 'Dark Mode'}
        </button>
      </div>
    </footer>
  );
};

export default Footer;
