import React, { useState } from 'react';
import axios from 'axios';
import './App.css';


function App() {
  const [number, setNumber] = useState('');
  const [result, setResult] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:3000/api/fizzbuzzbass', {
        number: parseInt(number),
      });
      setResult(response.data.result);
      setError(null);
    } catch (error) {
      setError("Error: Could not process your request");
    }
  }


  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100">
      <div className="bg-white p-6 rounded-lg shadow-md w-1/2">
        <h1 className="text-2xl font-bold mb-4">FizzBuzzBass</h1>
        <form onSubmit={handleSubmit} className="space-y-4">
          <input
            type="number"
            value={number}
            onChange={(e) => setNumber(e.target.value)}
            placeholder="Enter a number"
            className="border border-gray-300 p-2 w-full rounded-md"
          />
          <button
            type="submit"
            className="bg-blue-500 text-white p-2 w-full rounded-md hover:bg-blue-600"
          >
            Submit
          </button>
        </form>

        {result && (
          <div className="mt-4 p-4 bg-green-100 text-green-800 rounded-md">
            Result: {result}
          </div>
        )}

        {error && (
          <div className="mt-4 p-4 bg-red-100 text-red-800 rounded-md">
            {error}
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
