import React, { useState, useEffect } from "react";
import "../../index.css";

const Header = () => {

  const [darkMode, setDarkMode] = useState(false);

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
    if (!darkMode) {
      document.documentElement.classList.add("dark");
    } else {
      document.documentElement.classList.remove("dark");
    }
  };

  useEffect(() => {
    const userPrefersDark = window.matchMedia('(prefers-color-scheme: dark)').matches;
    if (userPrefersDark) {
      document.documentElement.classList.add("dark");
      setDarkMode(true);
    }
  }, []);

  return (
    <header className="bg-[#402E7A] dark:bg-gray-800 text-white py-6 shadow-md max-w-4xl mx-auto flex items-center justify-center">
      <div className="mx-auto flex justify-center items-center gap-4 mt-2 text-center max-w-max h-full">
        {/* Animated SVG Icon */}
        <svg
          xmlns="http://www.w3.org/2000/svg"
          viewBox="0 0 24 24"
          fill="none"
          stroke="white"
          strokeWidth="2"
          className="w-12 h-12 animate-spin-slow"
          aria-label="FizzBuzzBass Icon"
        >
        <path
          d="M12 2C6.48 2 2 6.48 2 12c0 5.52 4.48 10 10 10s10-4.48 10-10S17.52 2 12 2zm-1 17.93c-4.01-.47-7.18-3.87-7.72-7.93h7.72v7.93zM13 12V4.07c4.01.47 7.18 3.87 7.72 7.93h-7.72z"
          className="animated-path"
          />
        </svg>
        <h1 className="text-5xl font-extrabold tracking-tight">FIZZ-BUZZ-BASS</h1>
      </div>
    </header>
  );
};

export default Header;
